/** @format */

// Confetti.js
import React from 'react';
import Confetti from 'react-confetti';

const ConfettiComponent = () => {
  return (
    <Confetti
      width={window.innerWidth}
      height={window.innerHeight}
      numberOfPieces={200}
      recycle={false}
      gravity={0.2}
      run={true} // Imposta run a true per far durare l'animazione più a lungo
      wind={0.0} // Puoi sperimentare con il vento per controllare la velocità
    />
  );
};

export default ConfettiComponent;
