                      stopColor='#fff'
                    />
                  </linearGradient>
                  <mask id='mask1'>
                    <rect
                      x='0'
                      y='0'
                      width='160'
                      height='160'
                      fill='url(#grad)'
                    />
                  </mask>
                  <mask id='mask2'>
                    <rect
                      x='28'
                      y='28'
                      width='104'
                      height='104'
                      fill='url(#grad)'
                    />
                  </mask>
                </defs>

                <g>
                  <g className='pl__ring-rotate'>
                    <circle
                      className='pl__ring-stroke'
                      cx='80'
                      cy='80'
                      r='72'
                      fill='none'
                      stroke='hsl(223,90%,55%)'
                      strokeWidth='16'
                      strokeDasharray='452.39 452.39'
                      strokeDashoffset='452'
                      strokeLinecap='round'
                      transform='rotate(-45,80,80)'
                    />
                  </g>
                </g>
                <g mask='url(#mask1)'>
                  <g className='pl__ring-rotate'>
                    <circle
                      className='pl__ring-stroke'
                      cx='80'
