    setLoading(true); // Attiva l'indicatore di caricamento
    setDialogText('Cattura in corso...'); // Imposta il testo del dialogo solo durante il caricamento
    const interval = setInterval(() => {
      setDialogText(dialogText => dialogText + pokemonDialog[i]);
