/** @format */

import React, { useState } from 'react';
import './homepage.css';

const Homepage = () => {
  const URL = 'https://pokeapi.co/api/v2/pokemon/';
  const IdPokemon = {
    bug: '#26de81',
    dragon: '#ffeaa7',
    electric: '#fed330',
    fairy: '#FF0069',
    fighting: '#30336b',
    fire: '#f0932b',
    flying: '#81ecec',
    grass: '#00b894',
    ground: '#EFB549',
    ghost: '#a55eea',
    ice: '#74b9ff',
    normal: '#95afc0',
    poison: '#6c5ce7',
    psychic: '#a29bfe',
    rock: '#2d3436',
    water: '#0190FF',
  };
  const [pokemonData, setPokemonData] = useState([]);

  const getRandomPokemon = async () => {
    const randomPokemonData = [];
    for (let i = 0; i < 6; i++) {
      const id = Math.floor(Math.random() * 150) + 1;
      const finalUrl = URL + id;

      try {
        const response = await fetch(finalUrl);
        const data = await response.json();
        randomPokemonData.push(data);
      } catch (error) {
        console.error('Errore nella richiesta API:', error);
      }
    }

    setPokemonData(randomPokemonData);
  };

  const generateCard = (data, index) => {
    const hp = data.stats[0].base_stat;
    const imgSrc = data.sprites.other.dream_world.front_default;
    const pokeName = data.name[0].toUpperCase() + data.name.slice(1);
    const statAttack = data.stats[1].base_stat;
    const statDefense = data.stats[2].base_stat;
    const statSpeed = data.stats[5].base_stat;
    const themeColor = IdPokemon[data.types[0].type.name];

    return (
      <div
        key={index}
        className='card card-circle'
        style={{ backgroundColor: themeColor }}>
        <img
          src={imgSrc}
          alt={pokeName}
        />
      </div>
    );
  };

  return (
    <>
      <div className='container'>
        <div className='team'>
          {pokemonData.map((pokemon, index) => generateCard(pokemon, index))}
        </div>
        <div className='professore'>
          <img
            src='https://media.pokemoncentral.it/wiki/e/ed/Masters_Professor_Oak.png'
            alt=''
          />
        </div>
      </div>
      <div>
        <button onClick={getRandomPokemon}>Crea Squadra</button>
      </div>
    </>
  );
};

export default Homepage;
