import React, { useState, useEffect } from 'react';
import './homepage.css';

const Homepage = () => {
  const URL = 'https://pokeapi.co/api/v2/pokemon/';
  const IdPokemon = {
    bug: '#26de81',
    dragon: '#ffeaa7',
    electric: '#fed330',
    fairy: '#FF0069',
    fighting: '#30336b',
    fire: '#f0932b',
    flying: '#81ecec',
    grass: '#00b894',
    ground: '#EFB549',
    ghost: '#a55eea',
    ice: '#74b9ff',
    normal: '#95afc0',
    poison: '#6c5ce7',
    psychic: '#a29bfe',
    rock: '#2d3436',
    water: '#0190FF',
  };
  const [pokemonData, setPokemonData] = useState(null);

  useEffect(() => {
    getPokeData();
  }, []); // Empty dependency array to run once when component mounts

  const getPokeData = async () => {
    // Generate a random number between 1 and 150
    let id = Math.floor(Math.random() * 150) + 1;
    // Combine the pokeapi url with pokémon id
    const finalUrl = URL + id;

    try {
      // Fetch generated URL
      const response = await fetch(finalUrl);
      const data = await response.json();

      generateCard(data);
    } catch (error) {
      console.error('Errore nella richiesta API:', error);
    }
  };

  const generateCard = data => {
    // Get necessary data and assign it to variables
    const hp = data.stats[0].base_stat;
    const imgSrc = data.sprites.other.dream_world.front_default;
    const pokeName = data.name[0].toUpperCase() + data.name.slice(1);
    const statAttack = data.stats[1].base_stat;
    const statDefense = data.stats[2].base_stat;
    const statSpeed = data.stats[5].base_stat;

    // Set themeColor based on pokémon's type
    const themeColor = IdPokemon[data.types[0].type.name];

    // Set the data in the state
    setPokemonData({
      hp,
      imgSrc,
      pokeName,
      statAttack,
      statDefense,
      statSpeed,
      themeColor,
    });
  };

  return (
    <>
      <div className='container'>
        <div className='team'>
          {pokemonData &&
            Array.from({ length: 6 }).map((_, index) => (
              <div
                key={index}
                className='card card-circle'
                style={{ backgroundColor: pokemonData.themeColor }}>
                <img
                  src={pokemonData.imgSrc}
                  alt={pokemonData.pokeName}
                />
              </div>
            ))}
        </div>
        <div className='professore'>
          <img
            src='https://media.pokemoncentral.it/wiki/e/ed/Masters_Professor_Oak.png'
            alt=''
          />
        </div>
      </div>
      {/* <div className='dialogo'></div> */}
    </>
  );
};

export default Homepage;
